mapper.select:

	select 
	
		a.del_flag = 0
		
			and a.status = #{status}
		
		
		
			and a.rental_Type = #{rentalType}
		
		
		
			and a.year = #{year}
		
	
	order by a.year desc, a.month desc


	select count(1) from rental_order a
	
		
		
			
				and a.status = #{status}
			
			
			
				and a.rental_Type = #{rentalType}
